import React, { useEffect, useState } from "react";
import Button from "@material-ui/core/Button";

const Aladin = (props) => {
  /*
  const handlePopout = (props) =>{
  var object_title = props.name;
  var descFalse = "<b>AstroSat Observation:</b> ${props.astrosat}<br>";
  var descTrue = "<b>AstroSat Observation:</b> ${props.astrosat}<br> "
  }
  */

  useEffect(() => {
    let aladin = window.A.aladin("#aladin-lite-div", {
      survey: "P/DSS2/color",
      fov: 60,
    });
    let markerLayer = window.A.catalog();
    aladin.addCatalog(markerLayer);
    var sources = props.data.map((dataset) =>
      window.A.marker(dataset.ra, dataset.dec, {
        popupTitle: dataset.name,
        popupDesc:
          '<button id="myBtn">Open Modal</button><div id="myModal" class="modal"><div class="modal-content"><span class="close">&times;</span><p>Some text in the Modal..</p></div></div><script>var modal = document.getElementById("myModal");var btn = document.getElementById("myBtn");var span = document.getElementsByClassName("close")[0];btn.onclick = function() {modal.style.display = "block";}span.onclick = function(){modal.style.display = "none";}window.onclick = function(event) {if (event.target == modal) {modal.style.display = "none";}}</script>',
      })
    );
    markerLayer.addSources(sources);
  }, []);

  return (
    <div
      id="aladin-lite-div"
      style={{
        width: "74vw",
        height: "80vh",
      }}
    />
  );
};

export default Aladin;
