import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Modal from '@material-ui/core/Modal';

function getWindowDimensions() {
    const { innerWidth: width, innerHeight: height } = window;
    return {
      width,
      height
    };
  }

function getModalStyle() {
  const {topdim,leftdim} = getWindowDimensions();

  return {
    top: `50%`,
    left: `50%`,
    transform: `translate(-50%, -50%)`,
  };
}

const useStyles = makeStyles((theme) => ({
  paper: {
    position: 'absolute',
    width: 400,
    backgroundColor: theme.palette.background.paper,
    border: '2px solid #000',
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2, 4, 3),
  },
}));

export default function ModalCard() {
  const classes = useStyles();
  // getModalStyle is not a pure function, we roll the style only on the first render
  const [modalStyle] = React.useState(getModalStyle);
  const [open, setOpen] = React.useState(true);

  const handleOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const body = (
    <div style={modalStyle} className={classes.paper}>
      <h2 id="simple-modal-title">Welcome to AstroSat Visualizer!</h2>
      <p id="simple-modal-description">
      This site was designed by a team of sophomores at the Indian Institute of Technology Kanpur and is based on Aladin, an interactive sky atlas. We present a 3-D sky map of 280 X-Ray Binaries using Liu+ (2001).  The source names have been resolved using SIMBAD and is accurate upto atleast 2 arcmin!
<br/><br/>
Using our tool you can also find out whether the source has been observed by AstroSat within the last 2 years and you can also find publications corresponding to the source. COOL right? Contact our team if you wish to give any feedback. We would love to hear back from you. 
<br/><br/>
Happy Stargazing!

      </p>
    </div>
  );

  return (
    <div>
      <Modal
        open={open}
        onClose={handleClose}
        aria-labelledby="simple-modal-title"
        aria-describedby="simple-modal-description"
      >
        {body}
      </Modal>
    </div>
  );
}
