import React, { useEffect, useState } from "react";
import Button from "@material-ui/core/Button";

const Aladin = (props) => {
  function handlePopout(ra, dec, name, astrosat) {
    var object_title = name;
    /*
    var descFalse = `<b>AstroSat Observation:</b> ${astrosat}<br><button onClick="${console.log(
      "works"
    )}"> Click here for more info </button>`;

    var descTrue = `<b>AstroSat Observation:</b> ${astrosat}<br><button onClick="${console.log(
      "works"
    )}"> Click here for more info </button>`;*/

    var object_description = '<script>var modal=document.getElementById("myModal");var btn=document.getElementById("myBtn");function handleClick(){modal.style.display="block";} function handleClose(){modal.style.display="none";}</script><button id="myBtn" onclick="handleClick()">Open Modal</button><div id="myModal" class="modal" style="display: none; position: fixed; z-index: 1;padding-top: 100px;left: 0;top: 0;width: 100%; height: 100%; overflow: auto; background-color: rgb(0,0,0); background-color: rgba(0,0,0,0.4);"><div class="modal-content" style="position: relative;background-color:#fefefe;margin:auto;padding:0;border:1px solid #888;width:80%;box-shadow:0 4px 8px 0 rgba(0,0,0,0.2),0 6px 20px 0 rgba(0,0,0,0.19);"><div class="modal-header"> <span class="close" style="color: white;float:right;font-size:28px;font-weight:bold;">&times;</span><h2>Modal Header</h2></div><div class="modal-body"><p>Some text in the Modal Body</p><p>Some other text...</p></div><div class="modal-footer"><h3>Modal Footer</h3></div></div></div>';
    return window.A.marker(ra, dec, {
      popupTitle: object_title,
      popupDesc: object_description,
    });
  }

  useEffect(() => {
    let aladin = window.A.aladin("#aladin-lite-div", {
      survey: "P/DSS2/color",
      fov: 60,
    });
    let markerLayer = window.A.catalog();
    aladin.addCatalog(markerLayer);
    var sources = props.data.map((dataset) =>
      handlePopout(dataset.ra, dataset.dec, dataset.name, dataset.astrosat) 
    );
    markerLayer.addSources(sources);
  }, []);

  return (
    <div
      id="aladin-lite-div"
      style={{
        width: "74vw",
        height: "80vh",
      }}
    />
  );
};

export default Aladin;

