import React, { useEffect, useState } from "react";

const Aladin = (props) => {
  useEffect(() => {
    let aladin = window.A.aladin("#aladin-lite-div", {
      survey: "P/DSS2/color",
      fov: 1.5,
    });
    /*let markerLayer = window.A.catalog();
    aladin.addCatalog(markerLayer);
    var sources = props.data.map((dataset) =>
      window.A.marker(dataset.ra, dataset.dec)
    );
    markerLayer.addSources(sources);*/
    var marker1 = window.A.marker(270.332621, -23.078944, {
      popupTitle: "PSR B1758-23",
      popupDesc: "Object type: Pulsar",
    });
    var marker2 = window.A.marker(270.63206, -22.90555, {
      popupTitle: "HD 164514",
      popupDesc: "Object type: Star in cluster",
    });
    var marker3 = window.A.marker(270.598121, -23.030819, {
      popupTitle: "HD 164492",
      popupDesc: "Object type: Double star",
    });
    var markerLayer = window.A.catalog();
    aladin.addCatalog(markerLayer);
    markerLayer.addSources([marker1, marker2, marker3]);
  }, []);

  return (
    <div
      id="aladin-lite-div"
      style={{
        width: "74vw",
        height: "80vh",
      }}
    />
  );
};

export default Aladin;
