import React, { useEffect, useState } from "react";
import Button from "@material-ui/core/Button";

const Aladin = (props) => {
  function handlePopout(props) {
    var object_title = props.name;
    var descFalse = "<b>AstroSat Observation:</b> ${props.astrosat}<br>";
    var descTrue = "<b>AstroSat Observation:</b> ${props.astrosat}<br> ";

    var object_description = props.astrosat ? descTrue : descFalse;
    return window.A.marker(props.ra, props.dec, {
      popupTitle: object_title,
      popupDesc: object_description,
    });
  }

  useEffect(() => {
    let aladin = window.A.aladin("#aladin-lite-div", {
      survey: "P/DSS2/color",
      fov: 60,
    });
    let markerLayer = window.A.catalog();
    aladin.addCatalog(markerLayer);
    var sources = props.data.map((dataset) => handlePopout(dataset));
    markerLayer.addSources(sources);
  }, []);

  return (
    <div
      id="aladin-lite-div"
      style={{
        width: "74vw",
        height: "80vh",
      }}
    />
  );
};

export default Aladin;
